mapper.insert:
INSERT INTO COMPANY (
	COUNTRY
	, MARKET
	, TICKER
	, NAME
	, SECTOR
	, REMARK
)
VALUES(
	#{country}
	, #{market}
	, #{ticker}
	, #{name}
	, #{sector}
	, #{remark}
)

mapper.select:
SELECT (SELECT PROPERTIES FROM CD_CODE WHERE code = 'CM' AND MAIN_CATEGORY = a.COUNTRY) COUNTRY , (SELECT PROPERTIES FROM CD_CODE WHERE code = a.COUNTRY AND MAIN_CATEGORY = a.MARKET) MARKET , TICKER , NAME , (SELECT PROPERTIES FROM CD_CODE WHERE code = 'STOCK' AND MAIN_CATEGORY = a.SECTOR) SECTOR , REMARK FROM COMPANY a WHERE 1=1 AND COUNTRY = #{country} AND MARKET = #{market}
AND SECTOR = #{sector}
AND TICKER = #{ticker}

mapper.update:
UPDATE COMPANY
SET
	COUNTRY = #{country}
	, MARKET = #{market}
	, TICKER = #{ticker}
	, NAME = #{name}
	, SECTOR = #{sector}
	, REMARK = #{remark}
WHERE 1=1

mapper.delete:
DELETE
FROM COMPANY
WHERE 1=1
	AND COUNTRY = #{country}
	AND MARKET = #{market}
	AND TICKER = #{ticker}

mapper.select:
SELECT
	TICKER
	, NAME
FROM
	COMPANY
WHERE 1=1
	AND COUNTRY = #{country}
	AND MARKET = #{market}

		AND SECTOR = #{sector}
